const getHours = ()=>{
    const date = new Date();
    const hello = ['Bom Dia','Boa tarde','Boa noite'];
    if(date.getHours() < 12){
       return hello[0];
    }
    if(date.getHours() > 12){
        return hello[1]
    }
    if(date.getHours() > 18){
        return hello[2];
    }
    return `${addZero(date.getHours())}:${addZero(date.getMinutes())}`
}
const addZero = (e:number)=>e < 10? `0${e}` : e
export default getHours;
