import styled from 'styled-components';
export const HoursContainer = styled.section`
    padding: 100px;
    color: white;
    height: auto;
    background-color: black;
    width: 300px;
    padding: 50px;
    text-align: center;
`;
export const HoursContainerText = styled.p<{color: string}>`
    font-size: 15px;
    transition: 1s;
    border-radius: 10px;
    color: ${props=>props.color}
`;
